import { useState } from "react";
import { Lock } from 'lucide-react';
// import { Button } from "@/components/ui/button";
// import { Card } from "@/components/ui/card";
// import { Input } from "@/components/ui/input";
// import { Label } from "@/components/ui/label";

export default function PasswordReset() {
  const [error, setError] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const newPassword = formData.get("newPassword");
    const confirmPassword = formData.get("confirmPassword");

    if (newPassword !== confirmPassword) {
      setError("Passwords do not match");
      return;
    }

    setError("");
  };

  return (
    <div className="min-h-screen grid place-items-center bg-slate-100 p-4">
      <Card className="w-full max-w-4xl grid md:grid-cols-2 overflow-hidden">
        {/* Left side with gradient and lock icon */}
        <div className="relative bg-gradient-to-br from-blue-400 to-blue-600 p-8 text-white flex flex-col items-center justify-center">
          <div className="absolute inset-0 bg-blue-600/10 backdrop-blur-sm" />
          <div className="relative z-10 text-center">
            <div className="bg-white/10 p-6 rounded-full inline-block mb-4">
              <Lock className="w-12 h-12" />
            </div>
            <h2 className="text-xl md:text-2xl font-medium max-w-xs mx-auto">
              Enter your new password below to complete the reset process
            </h2>
          </div>
          {/* Decorative mail icons */}
          <div className="absolute inset-0 overflow-hidden">
            {[...Array(6)].map((_, i) => (
              <div
                key={i}
                className="absolute animate-float"
                style={{
                  left: `${Math.random() * 100}%`,
                  top: `${Math.random() * 100}%`,
                  animationDelay: `${Math.random() * 5}s`,
                }}
              >
                <div className="w-8 h-8 bg-white/10 rounded-lg transform rotate-45" />
              </div>
            ))}
          </div>
        </div>

        {/* Right side with form */}
        <div className="p-8">
          <div className="space-y-6">
            <div>
              <h1 className="text-2xl font-bold">Create a New Password</h1>
              <p className="text-gray-500 mt-2">
                Your new password must different from previous used password
              </p>
            </div>

            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="newPassword">New Password</Label>
                <Input
                  id="newPassword"
                  name="newPassword"
                  type="password"
                  required
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="confirmPassword">Confirm password</Label>
                <Input
                  id="confirmPassword"
                  name="confirmPassword"
                  type="password"
                  required
                />
              </div>

              {error && <p className="text-red-500 text-sm">{error}</p>}

              <Button type="submit" className="w-full bg-blue-500 hover:bg-blue-600">
                Update your password
              </Button>
            </form>
          </div>
        </div>
      </Card>
    </div>
  );
}
