import React, { useState } from 'react';
import './styles.css';
import { useNavigate } from 'react-router-dom';


function Login() {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });


  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle login
  };

  const navigate = useNavigate();

  const Login = () => {
    navigate('/Login');
  };

  return (
    <div className="window-container">
      <div className="split-layout">
        <div className="image-side">
          <img src="/placeholder.svg?height=800&width=600" alt="People celebrating" className="background-image" />
          <div className="blue-overlay"></div>
        </div>
        <div className="form-side">
          <div className="form-container">
            <h1 className="title">Log In</h1>
            <p className="subtitle">Welcome Back</p>
            
            <form onSubmit={handleSubmit}>
              <div className="input-group">
                <input 
                  type="email" 
                  placeholder="Email Address"
                  className="form-input"
                />
              </div>
              <div className="input-group">
                <input 
                  type="password" 
                  placeholder="Password"
                  className="form-input"
                />
                <div className="forgot-link">
                  <a href="/forgot-password">FORGOT PASSWORD ?</a>
                </div>
              </div>
              
              <button type="submit" className="primary-button">
                Log In
                <span className="arrow">→</span>
              </button>
            </form>

            <div className="social-section">
              <p className="social-divider">OR CONNECT WITH SOCIAL MEDIA</p>
              
              <button className="social-button instagram">
                <img src="/instagram-icon.png" alt="Instagram" className="social-icon" />
                SIGN UP WITH INSTAGRAM
              </button>
              
              <button className="social-button facebook">
                <img src="./src/assets/facebook.png" alt="Facebook" className="social-icon" />
                SIGN UP WITH FACEBOOK
              </button>
              
              <button className="social-button google">
                <img src="/google-icon.png" alt="Google" className="social-icon" />
                SIGN UP WITH GOOGLE
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
