import React, { useState } from "react";
import { Link } from "react-router-dom";
import { UserPlus } from 'lucide-react';
import { Button } from "../../../components/ui/button";
import { Input } from "../../../components/ui/input";
import { Card } from "../../../components/ui/card";

function SignUpForm() {
  const [formData, setFormData] = useState({
    fullName: "",
    phoneNumber: "",
    email: "",
    password: "",
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <div className="min-h-screen grid grid-cols-1 md:grid-cols-2">
      <Card className="p-8 md:p-12">
        <div className="space-y-6 max-w-sm mx-auto">
          <div className="space-y-2 text-center">
            <h1 className="text-2xl font-bold tracking-tight">SIGN UP</h1>
            <p className="text-muted-foreground">CREATE A ACCOUNT</p>
          </div>

          <div className="flex justify-center">
            <div className="relative">
              <div className="h-24 w-24 rounded-full border-2 border-muted flex items-center justify-center bg-muted">
                <UserPlus className="h-8 w-8 text-muted-foreground" />
              </div>
              <button
                className="absolute bottom-0 right-0 rounded-full bg-primary p-2 text-primary-foreground"
                aria-label="Upload avatar"
              >
                <span className="text-xl">+</span>
              </button>
            </div>
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            <Input
              placeholder="FULL NAME"
              name="fullName"
              value={formData.fullName}
              onChange={handleChange}
              required
            />
            <Input
              placeholder="PHONE NUMBER"
              name="phoneNumber"
              type="tel"
              value={formData.phoneNumber}
              onChange={handleChange}
              required
            />
            <Input
              placeholder="EMAIL"
              name="email"
              type="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
            <Input
              placeholder="PASSWORD"
              name="password"
              type="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
            <Button type="submit" className="w-full">
              SIGN UP
            </Button>
          </form>

          <p className="text-center text-sm">
            Already have an account?{" "}
            <Link to="/signin" className="text-primary hover:underline">
              sign up
            </Link>
          </p>
        </div>
      </Card>

      <div 
        className="hidden md:block bg-cover bg-center" 
        style={{
          backgroundImage: "url('/placeholder.svg')",
          backgroundColor: "rgba(0, 120, 212, 0.8)",
          backgroundBlendMode: "overlay"
        }}
      />
    </div>
  );
}

export default SignUpForm;
