import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './styles.css';
import { Button } from "../ui/button";
import { Input } from "../ui/input";
import { Card } from "../ui/card";

const SignUp = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    fullName: '',
    phoneNumber: '',
    email: '',
    password: '',
    profilePic: null
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    setFormData({
      ...formData,
      profilePic: file
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    navigate('/verify-otp');
  };

  return (
    <div className="min-h-screen grid grid-cols-1 md:grid-cols-2">
      <Card className="p-8 md:p-12">
        <div className="space-y-6 max-w-sm mx-auto">
          <div className="space-y-2 text-center">
            <h1 className="text-2xl font-bold tracking-tight">SIGN UP</h1>
            <p className="text-muted-foreground">CREATE A ACCOUNT</p>
          </div>

          <div className="flex justify-center">
            <div className="relative">
              <div className="h-24 w-24 rounded-full border-2 border-muted flex items-center justify-center bg-muted overflow-hidden">
                {formData.profilePic ? (
                  <img 
                    src={URL.createObjectURL(formData.profilePic)} 
                    alt="Profile" 
                    className="w-full h-full object-cover"
                  />
                ) : (
                  <div className="text-3xl text-muted-foreground">+</div>
                )}
              </div>
              <input
                type="file"
                id="profile-upload"
                accept="image/*"
                onChange={handleImageUpload}
                className="hidden"
              />
              <label
                htmlFor="profile-upload"
                className="absolute bottom-0 right-0 rounded-full bg-primary p-2 text-primary-foreground cursor-pointer"
              >
                <span className="text-xl">+</span>
              </label>
            </div>
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            <Input
              type="text"
              name="fullName"
              placeholder="FULL NAME"
              value={formData.fullName}
              onChange={handleChange}
              required
            />
            <Input
              type="tel"
              name="phoneNumber"
              placeholder="PHONE NUMBER"
              value={formData.phoneNumber}
              onChange={handleChange}
              required
            />
            <Input
              type="email"
              name="email"
              placeholder="EMAIL"
              value={formData.email}
              onChange={handleChange}
              required
            />
            <Input
              type="password"
              name="password"
              placeholder="PASSWORD"
              value={formData.password}
              onChange={handleChange}
              required
            />
            <Button type="submit" className="w-full">
              SIGN UP
            </Button>
          </form>

          <p className="text-center text-sm">
            Already have an account?{" "}
            <a href="/login" className="text-primary hover:underline">
              Sign In
            </a>
          </p>
        </div>
      </Card>

      <div 
        className="hidden md:block bg-cover bg-center" 
        style={{
          backgroundImage: "url('/placeholder.svg')",
          backgroundColor: "rgba(0, 120, 212, 0.8)",
          backgroundBlendMode: "overlay"
        }}
      />
    </div>
  );
};

export default SignUp;
