import React, { useEffect, useState } from "react";
import { Pie } from "react-chartjs-2";
import settingImage from "./settingimage.jpg";
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from "chart.js";
import axios from "axios";
import { format } from "date-fns";
import "./TaskDashboard.css"; // Use an external CSS file

ChartJS.register(ArcElement, Tooltip, Legend);

const TaskDashboard = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [tasks, setTasks] = useState([]);

  const handleInputChange = (event) => setSearchTerm(event.target.value);
  const handleSubmit = (event) => {
    event.preventDefault();
    console.log("Search term:", searchTerm);
  };

  useEffect(() => {
    axios
      .get("http://localhost:8000/api/tasks")
      .then((response) => setTasks(response.data))
      .catch((err) => console.error(err));
  }, []);

  const attendanceData = [
    { date: "25-12-2024", inTime: "Christmas", outTime: "Christmas" },
    { date: "24-12-2024", inTime: "7:45", outTime: "15:20" },
    // Add more data...
  ];

  const pieChartData = {
    labels: ["Present", "Absent"],
    datasets: [
      {
        data: [75, 25],
        backgroundColor: ["#2E7D32", "#98FB98"],
        borderWidth: 0,
      },
    ],
  };

  const pieChartOptions = {
    plugins: {
      legend: {
        position: "bottom",
      },
    },
  };

  return (
    <div className="task-dashboard">
      <div className="dashboard-content">
        <form className="search-bar" onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Search..."
            value={searchTerm}
            onChange={handleInputChange}
            className="search-input"
          />
          <button type="submit" className="search-button">
            SUBMIT
          </button>
        </form>

        <div className="dashboard-grid">
          <div className="card-container">
            {tasks.map((task, index) => (
              <div className="card" key={index}>
                <h4>{format(new Date(task.createdAt), "dd-MM-yyyy")}</h4>
                <h4>{new Date(task.createdAt).toLocaleTimeString()}</h4>
                <hr />
                <h3>{task.name}</h3>
                <p><strong>Learning:</strong> {task.learning}</p>
                <p><strong>Start Time:</strong> {task.startTime}</p>
                <p><strong>End Time:</strong> {task.endTime}</p>
                <div>
                  <strong>Time Slots:</strong>
                  <ul>
                    {task.timeSlots.map((slot, i) => (
                      <li key={i}>
                        {slot.startTime} - {slot.endTime} ({slot.notes})
                      </li>
                    ))}
                  </ul>
                </div>
              </div>
            ))}
          </div>

          <div className="summary-container">
            <div className="pie-chart-container">
              <Pie data={pieChartData} options={pieChartOptions} />
            </div>

            <div className="attendance-container">
              <h3>My Attendance</h3>
              <table className="attendance-table">
                <thead>
                  <tr>
                    <th>Date</th>
                    <th>In Time</th>
                    <th>Out Time</th>
                  </tr>
                </thead>
                <tbody>
                  {attendanceData.map((record, index) => (
                    <tr key={index}>
                      <td>{record.date}</td>
                      <td>{record.inTime}</td>
                      <td>{record.outTime}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TaskDashboard;
