import React, { useState, useEffect } from 'react';
import { Pie } from 'react-chartjs-2';
import settingImage from './settingimage.jpg';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend);

const TaskDashboard = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [tasks, setTasks] = useState([]);
  const [attendanceData, setAttendanceData] = useState([]);
  
  useEffect(() => {
    // Fetch tasks data from the API
    fetch("http://localhost:8000/api/tasks")
      .then((response) => response.json())
      .then((data) => {
        setTasks(data.tasks || []);
        setAttendanceData(data.attendance || []);
      })
      .catch((error) => {
        console.error("Error fetching tasks:", error);
      });
  }, []);

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log("Search term:", searchTerm);
  };

  const pieChartData = {
    labels: ['Present', 'Absent'],
    datasets: [
      {
        data: [75, 25],
        backgroundColor: ['#2E7D32', '#98FB98'],
        borderWidth: 0,
      },
    ],
  };

  const pieChartOptions = {
    plugins: {
      legend: {
        position: 'bottom',
      },
    },
  };

  return (
    <div style={styles.container}>
      <div style={{ padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
        <form onSubmit={handleSubmit}>
          <div className="search-bar">
            <input
              type="text"
              placeholder="Search..."
              value={searchTerm}
              onChange={handleInputChange}
            />
            <button type="submit" className="submit-button">
              SUBMIT
            </button>
          </div>
        </form>

        <div style={{ display: 'flex', gap: '20px' }}>
          <div style={{ flex: 2 }}>
            {tasks.map((task, index) => (
              <div
                key={index}
                style={{
                  backgroundColor: 'white',
                  border: '1px solid #ccc',
                  borderRadius: '5px',
                  marginBottom: '20px',
                  padding: '15px',
                }}
              >
                <div
                  style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    marginBottom: '15px',
                  }}
                >
                  <span>{task.startTime}</span>
                  <span>Task Name</span>
                  <span>{task.endTime}</span>
                </div>
                <ul style={{ listStyle: 'none', padding: 0 }}>
                  {task.items.map((item, i) => (
                    <li key={i} style={{ marginBottom: '10px' }}>
                      <span style={{ marginRight: '10px' }}>•</span>
                      {item}
                    </li>
                  ))}
                </ul>
                <div style={{ display: 'flex', gap: '20px', marginTop: '15px' }}>
                  {task.references.map((ref, i) => (
                    <div key={i}>
                      <span style={{ marginRight: '10px' }}>•</span>
                      {ref}
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>

          <div style={{ flex: 1 }}>
            <div
              style={{
                border: '1px solid #ccc',
                borderRadius: '5px',
                padding: '15px',
                backgroundColor: 'white',
              }}
            >
              <div style={{ marginBottom: '20px', maxWidth: '400px' }}>
                <Pie data={pieChartData} options={pieChartOptions} />
              </div>
            </div>

            <div
              style={{
                border: '1px solid #ccc',
                borderRadius: '5px',
                padding: '15px',
                marginTop: '10px',
                backgroundColor: 'white',
              }}
            >
              <div
                style={{ display: 'flex', alignItems: 'center', marginBottom: '15px' }}
              >
                <span style={{ marginRight: '10px' }}>↓</span>
                <h3 style={{ margin: 0 }}>My Attendance</h3>
              </div>
              <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                <thead>
                  <tr>
                    <th style={{ padding: '10px', textAlign: 'left' }}>Date</th>
                    <th colSpan="2" style={{ padding: '10px', textAlign: 'center' }}>
                      Punch Time
                    </th>
                  </tr>
                  <tr>
                    <th></th>
                    <th style={{ padding: '10px', textAlign: 'center' }}>In Time</th>
                    <th style={{ padding: '10px', textAlign: 'center' }}>Out Time</th>
                  </tr>
                </thead>
                <tbody>
                  {attendanceData.map((record, index) => (
                    <tr key={index}>
                      <td style={{ padding: '10px' }}>{record.date}</td>
                      <td
                        style={{ padding: '10px', textAlign: 'center' }}
                      >
                        <span
                          style={{
                            backgroundColor:
                              record.inTime === 'Christmas'
                                ? '#40C4FF'
                                : '#2E7D32',
                            color: 'white',
                            padding: '3px 8px',
                            borderRadius: '3px',
                          }}
                        >
                          {record.inTime}
                        </span>
                      </td>
                      <td
                        style={{ padding: '10px', textAlign: 'center' }}
                      >
                        <span
                          style={{
                            backgroundColor:
                              record.outTime === 'Christmas'
                                ? '#40C4FF'
                                : '#2E7D32',
                            color: 'white',
                            padding: '3px 8px',
                            borderRadius: '3px',
                          }}
                        >
                          {record.outTime}
                        </span>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    minHeight: '100vh',
    backgroundColor: '#f5f5f5',
    backgroundImage: `url(${settingImage})`,
    backgroundRepeat: 'no-repeat',
    backgroundSize: 'cover',
    fontFamily: 'Arial, sans-serif',
  },
};

export default TaskDashboard;
