import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './styles.css'; // Import the CSS file here

const ResetPassword = () => {
  const [passwords, setPasswords] = useState({
    newPassword: '',
    confirmPassword: ''
  });

  const handleChange = (e) => {
    setPasswords({
      ...passwords,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle password update logic here
    console.log('Updating password:', passwords);
  };

  return (
    <div style={styles.container}>
      <div style={styles.leftSection}>
        <div style={styles.overlay}>
          <div style={styles.lockIconContainer}>
            {/* You can replace this with an actual lock icon */}
            <div style={styles.lockIcon}></div>
          </div>
          <p style={styles.leftText}>
            Enter your new password below to complete the reset process
          </p>
        </div>
      </div>
      
      <div style={styles.rightSection}>
        <div style={styles.formContainer}>
          <h1 style={styles.heading}>Create a New Password</h1>
          <p style={styles.subText}>
            Your new password must different from previous used password
          </p>
          
          <form onSubmit={handleSubmit} style={styles.form}>
            <div style={styles.inputGroup}>
              <input
                type="password"
                name="newPassword"
                placeholder="New Password"
                value={passwords.newPassword}
                onChange={handleChange}
                style={styles.input}
                required
              />
            </div>
            
            <div style={styles.inputGroup}>
              <input
                type="password"
                name="confirmPassword"
                placeholder="Confirm - password"
                value={passwords.confirmPassword}
                onChange={handleChange}
                style={styles.input}
                required
              />
            </div>
            
            <button type="submit" style={styles.button}>
              Update your password
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};
