import React, { useState } from 'react';

const ResetPassword = () => {
  const [passwords, setPasswords] = useState({
    newPassword: '',
    confirmPassword: ''
  });

  const handleChange = (e) => {
    setPasswords({
      ...passwords,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Updating password:', passwords);
  };

  return (
    <div style={styles.container}>
      <div style={styles.imageSection}>
        <div style={styles.overlay}>
          <p style={styles.overlayText}>
            Enter your new password below to complete the reset process
          </p>
        </div>
      </div>
      <div style={styles.formSection}>
        <div style={styles.formContent}>
          <h1 style={styles.title}>Create a New Password</h1>
          <p style={styles.subtitle}>
            Your new password must different from previous used password
          </p>

          <form onSubmit={handleSubmit} style={styles.form}>
            <input
              type="password"
              name="newPassword"
              placeholder="New Password"
              value={passwords.newPassword}
              onChange={handleChange}
              style={styles.input}
              required
            />
            <input
              type="password"
              name="confirmPassword"
              placeholder="Confirm - password"
              value={passwords.confirmPassword}
              onChange={handleChange}
              style={styles.input}
              required
            />
            <button type="submit" style={styles.button}>
              Update your password
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: 'grid',
    gridTemplateColumns: '1fr 1fr',
    minHeight: '100vh',
    fontFamily: 'Arial, sans-serif'
  },
  imageSection: {
    position: 'relative',
    backgroundColor: '#0078D4',
    backgroundImage: `url('/placeholder.svg?height=800&width=600')`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '2rem'
  },
  overlay: {
    textAlign: 'center',
    color: 'white',
    zIndex: 1
  },
  overlayText: {
    fontSize: '24px',
    maxWidth: '400px',
    lineHeight: '1.5'
  },
  formSection: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '2rem'
  },
  formContent: {
    width: '100%',
    maxWidth: '400px'
  },
  title: {
    fontSize: '24px',
    fontWeight: 'bold',
    marginBottom: '12px'
  },
  subtitle: {
    color: '#666',
    marginBottom: '32px'
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: '20px'
  },
  input: {
    padding: '12px',
    border: 'none',
    borderBottom: '1px solid #ddd',
    outline: 'none',
    fontSize: '14px'
  },
  button: {
    backgroundColor: '#0078D4',
    color: 'white',
    padding: '12px',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    fontSize: '14px',
    marginTop: '12px'
  }
};

export default ResetPassword;
