            </p>

            <div className="otp-container">
              {otp.map((digit, index) => (
                <input
                  key={index}
                  type="text"
                  maxLength="1"
                  value={digit}
                  onChange={(e) => handleChange(index, e.target.value)}
                  // ref={(el) => inputRefs.current[index] = el}
                  className="otp-input"
                />
              ))}
            </div>

            {/* {error && <p className="error-message">{error}</p>} */}
            <button type="submit" className="primary-button" onClick={verifyOtp}>
              Verify OTP
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

