import React, { useState } from 'react';
import './styles.css';
import { useNavigate } from 'react-router-dom';

function SignUp() {
  const [formData, setFormData] = useState({
    fullName: '',
    phoneNumber: '',
    email: '',
    password: '',
    profileImage: null
  });

  const navigate = useNavigate();

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData({
        ...formData,
        profileImage: URL.createObjectURL(file)
      });
    }
  };

  const handleRedirectToLogin = (e) => {
    e.preventDefault();
    navigate('/login'); // Use the route path for the Login component
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:8000/api/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });
      if (response.ok) {
        // Handle successful signup (e.g., redirect or show a success message)
        navigate('/login');
      } else {
        // Handle errors (e.g., show an error message)
        console.error('Signup failed');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="window-container">
      <div className="split-layout">
        <div className="form-side">
          <div className="form-container">
            <h1 className="title">SIGN UP</h1>
            <p className="subtitle">CREATE AN ACCOUNT</p>
            <div className="profile-upload">
              <div className="profile-circle">
                {formData.profileImage ? (
                  <img src={formData.profileImage} alt="Profile" className="profile-preview" />
                ) : (
                  <div className="profile-placeholder">
                    <span className="plus-icon">+</span>
                  </div>
                )}
              </div>
              <input
                type="file"
                id="profile-upload"
                accept="image/*"
                onChange={handleImageUpload}
                className="hidden"
              />
            </div>

            <form>
              <div className="input-group" >
                <input 
                  type="text" 
                  placeholder="FULL NAME"
                  className="form-input"
                />
              </div>
              <div className="input-group">
                <input 
                  type="tel" 
                  placeholder="PHONE NUMBER"
                  className="form-input"
                />
              </div>
              <div className="input-group">
                <input 
                  type="email" 
                  placeholder="EMAIL"
                  className="form-input"
                />
              </div>
              <div className="input-group">
                <input 
                  type="password" 
                  placeholder="PASSWORD"
                  className="form-input"
                />
              </div>
              
              <button type="submit" className="primary-button">
                SIGN UP
              </button>
            </form>

            <p className="auth-link">
              Already have an account? <a href="/login" onClick={handleRedirectToLogin}>Sign In</a>
            </p>
          </div>
        </div>
        <div className="image-side1">
          <div className="blue-overlay"></div>
        </div>
      </div>
    </div>
  );
}

export default SignUp;
