import React, { useState } from 'react';
import './styles.css';
import { useNavigate } from 'react-router-dom';

function SignUp() {
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    email: '',
    password: '',
    profile_pic: null, // Store the uploaded profile image
  });

  const navigate = useNavigate();

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    const maxSize = 300 * 1024; // 300 KB in bytes

    if (file && file.type.startsWith('image/')) {
      // Check if the file size is within the limit (<= 300 KB)
      if (file.size > maxSize) {
        alert('The image size should be less than or equal to 300 KB.');
      } else {
        setFormData({
          ...formData,
          profileImage: URL.createObjectURL(file), // Set the uploaded image for preview
        });
        alert('Image uploaded successfully!'); // Show success alert only if within size
      }
    } else {
      alert('Please upload a valid image.');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleRedirectToLogin = (e) => {
    e.preventDefault();
    navigate('/login'); // Use the route path for the Login component
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:8000/api/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });
      if (response.ok) {
        // Handle successful signup (e.g., redirect or show a success message)
        navigate('/login');
      } else {
        // Handle errors (e.g., show an error message)
        console.error('Signup failed');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="window-container">
      <div className="split-layout">
        <div className="form-side">
          <div className="form-container">
            <h1 className="title">SIGN UP</h1>
            <p className="subtitle">CREATE AN ACCOUNT</p>
            <div className="profile-upload">
              <label htmlFor="profile-upload">
                <div className="profile-circle">
                  {formData.profileImage ? (
                    <img
                      src={formData.profileImage}
                      alt="Profile"
                      className="profile-preview"
                    />
                  ) : (
                    <div className="profile-placeholder">
                      <span className="plus-icon">+</span>
                    </div>
                  )}
                </div>
              </label>
              <input
                type="file"
                id="profile-upload"
                name="profile_pic"
                accept="image/*"
                onChange={handleImageUpload}
                style={{ display: 'none' }} // Make the input invisible
              />
            </div>

            <form onSubmit={handleSubmit}>
              
            <div className="input-group">
                <input
                  type="text"
                  name="name" // Add name attribute
                  placeholder="FULL NAME"
                  className="form-input"
                  onChange={handleInputChange} // Update state on change
                />
              </div>
              <div className="input-group">
                <input
                  type="tel"
                  name="phone" // Add name attribute
                  placeholder="PHONE NUMBER"
                  className="form-input"
                  onChange={handleInputChange} // Update state on change
                />
              </div>
              <div className="input-group">
                <input
                  type="email"
                  name="email" // Add name attribute
                  placeholder="EMAIL"
                  className="form-input"
                  onChange={handleInputChange} // Update state on change
                />
              </div>
              <div className="input-group">
                <input
                  type="password"
                  name="password" // Add name attribute
                  placeholder="PASSWORD"
                  className="form-input"
                  onChange={handleInputChange} // Update state on change
                />
              </div>

              <button type="submit" className="primary-button">
                SIGN UP
              </button>
            </form>

            <p className="auth-link">
              Already have an account? <a href="/login" onClick={handleRedirectToLogin}>Sign In</a>
            </p>
          </div>
        </div>
        <div className="image-side1">
          <div className="blue-overlay"></div>
        </div>
      </div>
    </div>
  );
}

export default SignUp;
