import React, { useState } from 'react';
import { Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
// import '../ui/client/src/ui/reshot-icon-calendar-RPX8GDWZN9.svg';

ChartJS.register(ArcElement, Tooltip, Legend);

const TaskDashboard = () => {
  const [searchTerm, setSearchTerm] = useState("");

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Perform search action with searchTerm
    console.log("Search term:", searchTerm);
  };

  const tasks = [
    {
      startTime: '09:10:51',
      endTime: '04:57:12',
      items: [
        'Create the chat application',
        'Create the login and signup screen',
        'Create the database and user’s login in the database and successfully login',
      ],
      references: ['Chat application Refer', 'Chat application Privacy refer'],
    },
    {
      startTime: '10:00:00',
      endTime: '05:00:00',
      items: [
        'Implement real-time messaging',
        'Set up user authentication',
        'Design the user interface',
      ],
      references: ['UI Design Guidelines', 'Authentication API Docs'],
    },
  ];

  const attendanceData = [
    { date: '25-12-2024', inTime: 'Christmas', outTime: 'Christmas' },
    { date: '24-12-2024', inTime: '7:45', outTime: '15:20' },
    { date: '23-12-2024', inTime: '8:00', outTime: '14:14' },
    { date: '22-12-2024', inTime: '7:35', outTime: '12:30' },
    { date: '21-12-2024', inTime: '8:20', outTime: '15:55' },
    { date: '20-12-2024', inTime: '7:55', outTime: '15:20' },
    { date: '19-12-2024', inTime: '8:30', outTime: '15:20' }
  ];

  const pieChartData = {
    labels: ['Present', 'Absent'],
    datasets: [
      {
        data: [75, 25],
        backgroundColor: [
          '#2E7D32',
          '#98FB98'
        ],
        borderWidth: 0
      }
    ]
  };

  const pieChartOptions = {
    plugins: {
      legend: {
        position: 'bottom'
      }
    }
  };

  return (
    <div style={{ padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
      <form onSubmit={handleSubmit}>
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search..."
          value={searchTerm}
          onChange={handleInputChange}
        />
        <button type="submit" className="submit-button">
          SUBMIT
        </button>
      </div>
    </form>

      <div style={{ display: 'flex', gap: '20px' }}>
      <div>
      <div style={{ display: 'flex', alignItems: 'center', margin: '10px 0' }}>
        <img src="reshot-icon-calendar-RPX8GDWZN9.svg" alt="Calendar Icon" style={{ marginRight: '5px' }} />
        <span>24 - Dec - 2024</span>
      </div>
      <div style={{ display: 'flex', justifyContent: 'space-between', margin: '10px 0' }}>
        <span>09:10:51</span>
        <span>Task Name</span>
        <span>04:57:12</span>
      </div>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        <li>Create the chat application</li>
        <li>Create the login and signup screen</li>
        <li>Create the database and user's login in the database and successfully login</li>
      </ul>
      <div style={{ display: 'flex', justifyContent: 'space-between', margin: '10px 0' }}>
        <div>
          <span style={{ display: 'block', margin: '0 0 5px 0' }}>Chat application Refer</span>
          <div style={{ backgroundColor: 'black', width: '10px', height: '10px', borderRadius: '50%' }} />
        </div>
        <div>
          <span style={{ display: 'block', margin: '0 0 5px 0' }}>Chat application Privacy refer</span>
          <div style={{ backgroundColor: 'black', width: '10px', height: '10px', borderRadius: '50%' }} />
        </div>
      </div>

      <div style={{ display: 'flex', alignItems: 'center', margin: '10px 0' }}>
        <img src="reshot-icon-calendar-RPX8GDWZN9.svg" alt="Calendar Icon" style={{ marginRight: '5px' }} />
        <span>27 - Dec - 2024</span>
      </div>
      <div style={{ display: ' flex', justifyContent: 'space-between', margin: '10px 0' }}>
        <span>10:00:00</span>
        <span>Task Name</span>
        <span>05:00:00</span>
      </div>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        <li>Implement real-time messaging</li>
        <li>Set up user authentication</li>
        <li>Design the user interface</li>
      </ul>
      <div style={{ display: 'flex', justifyContent: 'space-between', margin: '10px 0' }}>
        <div>
          <span style={{ display: 'block', margin: '0 0 5px 0' }}>UI Design Guidelines</span>
          <div style={{ backgroundColor: 'black', width: '10px', height: '10px', borderRadius: '50%' }} />
        </div>
        <div>
          <span style={{ display: 'block', margin: '0 0 5px 0' }}>Authentication API Docs</span>
          <div style={{ backgroundColor: 'black', width: '10px', height: '10px', borderRadius: '50%' }} />
        </div>
      </div>
    </div>

        <div style={{ flex: 1 }}>
          <div style={{ marginBottom: '20px', maxWidth: '400px' }}>
            <Pie data={pieChartData} options={pieChartOptions} />
          </div>

          <div style={{ border: '1px solid #ccc', borderRadius: '5px', padding: '15px' }}>
            <div style={{ display: 'flex', alignItems: 'center', marginBottom: '15px' }}>
              <span style={{ marginRight: '10px' }}>↓</span>
              <h3 style={{ margin: 0 }}>My Attendance</h3>
            </div>
            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
              <thead>
                <tr>
                  <th style={{ padding: '10px', textAlign: 'left' }}>Date</th>
                  <th colSpan="2" style={{ padding: '10px', textAlign: 'center' }}>Punch Time</th>
                </tr>
                <tr>
                  <th></th>
                  <th style={{ padding: '10px', textAlign: 'center' }}>In Time</th>
                  <th style={{ padding: '10px', textAlign: 'center' }}>Out Time</th>
                </tr>
              </thead>
              <tbody>
                {attendanceData.map((record, index) => (
                  <tr key={index}>
                    <td style={{ padding: '10px' }}>{record.date}</td>
                    <td style={{ padding: '10px', textAlign: 'center' }}>
                      <span style={{
                        backgroundColor: record.inTime === 'Christmas' ? '#40C4FF' : '#2E7D32',
                        color: 'white',
                        padding: '3px 8px',
                        borderRadius: '3px'
                      }}>
                        {record.inTime}
                      </span>
                    </td>
                    <td style={{ padding: '10px', textAlign: 'center' }}>
                      <span style={{
                        backgroundColor: record.outTime === 'Christmas' ? '#40C4FF' : '#2E7D32',
                        color: 'white',
                        padding: '3px 8px',
                        borderRadius: '3px'
                      }}>
                        {record.outTime}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TaskDashboard;
