import React from 'react';
import { Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { Download } from 'lucide-react';

ChartJS.register(ArcElement, Tooltip, Legend);

const TaskDashboard = () => {
  const tasks = [
    {
      date: '24 - Dec - 2024',
      items: [
        'Create the chat application',
        'Create the login and signup screen',
        'Create the database and user\'s login in the database and successfully login'
      ],
      references: ['Chat application Refer', 'Chat application Privacy refer'],
      startTime: '09:10:51',
      endTime: '04:57:12'
    },
    {
      date: '27 - Dec - 2024',
      items: [
        'Create the chat application',
        'Create the login and signup screen',
        'Create the database and user\'s login in the database and successfully login'
      ],
      references: ['Chat application Refer', 'Chat application Privacy refer'],
      startTime: '09:10:51',
      endTime: '04:57:12'
    }
  ];

  const attendanceData = [
    { date: '25-12-2024', inTime: 'Christmas', outTime: 'Christmas' },
    { date: '24-12-2024', inTime: '7:45', outTime: '15:20' },
    { date: '23-12-2024', inTime: '8:00', outTime: '14:14' },
    { date: '22-12-2024', inTime: '7:35', outTime: '12:30' },
    { date: '21-12-2024', inTime: '8:20', outTime: '15:55' },
    { date: '20-12-2024', inTime: '7:55', outTime: '15:20' },
    { date: '19-12-2024', inTime: '8:30', outTime: '15:20' }
  ];

  const pieChartData = {
    labels: ['Present', 'Absent'],
    datasets: [
      {
        data: [75, 25],
        backgroundColor: [
          '#2E7D32',
          '#98FB98'
        ],
        borderWidth: 0
      }
    ]
  };

  const pieChartOptions = {
    plugins: {
      legend: {
        position: 'bottom'
      }
    }
  };

  return (
    <div className="max-w-7xl mx-auto px-4 py-8">
      {/* Search Bar */}
      <div className="flex gap-4 mb-8">
        <div className="flex-1">
          <div className="relative">
            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
              <svg className="h-5 w-5 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
              </svg>
            </div>
            <input
              type="search"
              className="block w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md leading-5 bg-white placeholder-gray-500 focus:outline-none focus:ring-1 focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
              placeholder="Search..."
            />
          </div>
        </div>
        <button className="px-6 py-2 bg-[#40C4FF] text-white rounded-md hover:bg-[#00B0FF] transition-colors">
          SUBMIT
        </button>
      </div>

      <div className="flex gap-8">
        {/* Tasks Section */}
        <div className="flex-[2] space-y-6">
          {tasks.map((task, index) => (
            <div key={index} className="bg-white rounded-lg shadow-sm border border-gray-200 overflow-hidden">
              <div className="border-b border-gray-200 px-6 py-4 flex justify-between items-center">
                <span className="text-gray-600">{task.startTime}</span>
                <span className="font-medium">Task Name</span>
                <span className="text-gray-600">{task.endTime}</span>
              </div>
              <div className="px-6 py-4">
                <ul className="space-y-3">
                  {task.items.map((item, i) => (
                    <li key={i} className="flex items-start">
                      <span className="mr-2 mt-1.5">•</span>
                      <span>{item}</span>
                    </li>
                  ))}
                </ul>
                <div className="mt-4 flex gap-6">
                  {task.references.map((ref, i) => (
                    <div key={i} className="flex items-center">
                      <span className="mr-2">•</span>
                      <span className="text-gray-700">{ref}</span>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Charts and Attendance Section */}
        <div className="flex-1">
          <div className="mb-8">
            <Pie data={pieChartData} options={pieChartOptions} />
          </div>

          <div className="bg-white rounded-lg shadow-sm border border-gray-200">
            <div className="px-6 py-4 border-b border-gray-200 flex items-center">
              <Download className="w-5 h-5 mr-2" />
              <h3 className="font-medium">My Attendance</h3>
            </div>
            <div className="p-4">
              <table className="w-full">
                <thead>
                  <tr>
                    <th className="text-left py-2">Date</th>
                    <th colSpan={2} className="text-center py-2">Punch Time</th>
                  </tr>
                  <tr className="border-b">
                    <th></th>
                    <th className="text-center py-2">In Time</th>
                    <th className="text-center py-2">Out Time</th>
                  </tr>
                </thead>
                <tbody>
                  {attendanceData.map((record, index) => (
                    <tr key={index} className="border-b last:border-b-0">
                      <td className="py-2">{record.date}</td>
                      <td className="text-center py-2">
                        <span className={`inline-block px-3 py-1 rounded text-white ${
                          record.inTime === 'Christmas' ? 'bg-[#40C4FF]' : 'bg-[#2E7D32]'
                        }`}>
                          {record.inTime}
                        </span>
                      </td>
                      <td className="text-center py-2">
                        <span className={`inline-block px-3 py-1 rounded text-white ${
                          record.outTime === 'Christmas' ? 'bg-[#40C4FF]' : 'bg-[#2E7D32]'
                        }`}>
                          {record.outTime}
                        </span>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TaskDashboard;
