import React, { useState } from 'react';
import './styles.css';
import { useNavigate } from 'react-router-dom';


function Login() {

  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    email: '',
    password: '',
    profile_pic: ''
});

const navigate = useNavigate();

const handleImageUpload = (e) => {
  const file = e.target.files[0];
  const maxSize = 300 * 1024; // 300 KB in bytes

  if (file && file.type.startsWith('image/')) {
    if (file.size > maxSize) {
      alert('The image size should be less than or equal to 300 KB.');
    } else {
      setFormData({
        ...formData,
        profile_pic: URL.createObjectURL(file), // Set the uploaded image for preview
      });
      alert('Image uploaded successfully!');
    }
  } else {
    alert('Please upload a valid image.');
  }
};


const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
        ...formData,
        [name]: value
    });
}; 

const handleSubmit = (e) => {
  e.preventDefault();

  // POST request using fetch instead of axios
  fetch('http://localhost:8000/api/register', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(formData), // Convert formData to JSON
  })
    .then(async (response) => {
      if (response.ok) {
        const data = await response.json();
        alert(data.message || 'Registration successful!');
        navigate('/Login'); // Redirect to login page
      } else {
        const errorData = await response.json();
        console.error('Signup failed:', errorData);
        alert('Signup failed: ' + errorData.message);
      }
    })
    .catch((error) => {
      console.error('An error occurred:', error);
      alert('An unexpected error occurred. Please try again.');
    });
};

  const forgot = () => {
    navigate('/ForgotPassword');
  };

  const taskmanager = () => {
    navigate('/TaskManager');
  };

  return (
    <div className="window-container">
      <div className="split-layout">
        <div className="image-side">
          <div className="blue-overlay"></div>
        </div>
        <div className="form-side">
          <div className="form-container">
            <h1 className="title">Log In</h1>
            <p className="subtitle">Welcome Back</p>
            
            <form onSubmit={handleSubmit}>
              <div className="input-group">
                <input 
                  type="email" 
                  placeholder="Email Address"
                  className="form-input"
                />
              </div>
              <div className="input-group">
                <input 
                  type="password" 
                  placeholder="Password"
                  className="form-input"
                />
                <div className="forgot-link">
                  <a href="/ForgotPassword" onClick={forgot}>FORGOT PASSWORD ?</a>
                </div>
              </div>
              
              <button type="submit" className="primary-button" onClick={taskmanager}>
                Log In
                <span className="arrow">â†’</span>
              </button>
            </form>

            <div className="social-section">
              <p className="social-divider">OR CONNECT WITH SOCIAL MEDIA</p>
              
              <button className="social-button instagram">
              <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-instagram" viewBox="0 0 16 16">
  <path d="M8 0C5.829 0 5.556.01 4.703.048 3.85.088 3.269.222 2.76.42a3.9 3.9 0 0 0-1.417.923A3.9 3.9 0 0 0 .42 2.76C.222 3.268.087 3.85.048 4.7.01 5.555 0 5.827 0 8.001c0 2.172.01 2.444.048 3.297.04.852.174 1.433.372 1.942.205.526.478.972.923 1.417.444.445.89.719 1.416.923.51.198 1.09.333 1.942.372C5.555 15.99 5.827 16 8 16s2.444-.01 3.298-.048c.851-.04 1.434-.174 1.943-.372a3.9 3.9 0 0 0 1.416-.923c.445-.445.718-.891.923-1.417.197-.509.332-1.09.372-1.942C15.99 10.445 16 10.173 16 8s-.01-2.445-.048-3.299c-.04-.851-.175-1.433-.372-1.941a3.9 3.9 0 0 0-.923-1.417A3.9 3.9 0 0 0 13.24.42c-.51-.198-1.092-.333-1.943-.372C10.443.01 10.172 0 7.998 0zm-.717 1.442h.718c2.136 0 2.389.007 3.232.046.78.035 1.204.166 1.486.275.373.145.64.319.92.599s.453.546.598.92c.11.281.24.705.275 1.485.039.843.047 1.096.047 3.231s-.008 2.389-.047 3.232c-.035.78-.166 1.203-.275 1.485a2.5 2.5 0 0 1-.599.919c-.28.28-.546.453-.92.598-.28.11-.704.24-1.485.276-.843.038-1.096.047-3.232.047s-2.39-.009-3.233-.047c-.78-.036-1.203-.166-1.485-.276a2.5 2.5 0 0 1-.92-.598 2.5 2.5 0 0 1-.6-.92c-.109-.281-.24-.705-.275-1.485-.038-.843-.046-1.096-.046-3.233s.008-2.388.046-3.231c.036-.78.166-1.204.276-1.486.145-.373.319-.64.599-.92s.546-.453.92-.598c.282-.11.705-.24 1.485-.276.738-.034 1.024-.044 2.515-.045zm4.988 1.328a.96.96 0 1 0 0 1.92.96.96 0 0 0 0-1.92m-4.27 1.122a4.109 4.109 0 1 0 0 8.217 4.109 4.109 0 0 0 0-8.217m0 1.441a2.667 2.667 0 1 1 0 5.334 2.667 2.667 0 0 1 0-5.334"/>
</svg>
                SIGN UP WITH INSTAGRAM
              </button>
              
              <button className="social-button facebook">
              <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-facebook" viewBox="0 0 16 16">
  <path d="M16 8.049c0-4.446-3.582-8.05-8-8.05C3.58 0-.002 3.603-.002 8.05c0 4.017 2.926 7.347 6.75 7.951v-5.625h-2.03V8.05H6.75V6.275c0-2.017 1.195-3.131 3.022-3.131.876 0 1.791.157 1.791.157v1.98h-1.009c-.993 0-1.303.621-1.303 1.258v1.51h2.218l-.354 2.326H9.25V16c3.824-.604 6.75-3.934 6.75-7.951"/>
</svg>
                SIGN UP WITH FACEBOOK
              </button>
              
              <button className="social-button google">
              <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-google" viewBox="0 0 16 16">
  <path d="M15.545 6.558a9.4 9.4 0 0 1 .139 1.626c0 2.434-.87 4.492-2.384 5.885h.002C11.978 15.292 10.158 16 8 16A8 8 0 1 1 8 0a7.7 7.7 0 0 1 5.352 2.082l-2.284 2.284A4.35 4.35 0 0 0 8 3.166c-2.087 0-3.86 1.408-4.492 3.304a4.8 4.8 0 0 0 0 3.063h.003c.635 1.893 2.405 3.301 4.492 3.301 1.078 0 2.004-.276 2.722-.764h-.003a3.7 3.7 0 0 0 1.599-2.431H8v-3.08z"/>
</svg>
                SIGN UP WITH GOOGLE
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
