import React, { useState, useRef } from 'react';
import './styles.css';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const otpfile = require('./otpfile.png');

function OTPVerification() {
    const navigate = useNavigate();
    const [otp, setOtp] = useState(['', '', '', '', '', '']);
    const inputRefs = useRef([]);
    const [error, setError] = useState(null);

    // Handle OTP input change
    const handleChange = (index, value) => {
        if (isNaN(value) || value.length > 1) return; // Allow only single digits

        const newOtp = [...otp];
        newOtp[index] = value;
        setOtp(newOtp);

        // Focus on next input
        if (value && index < 5) {
            inputRefs.current[index + 1]?.focus();
        }
    };

    // Verify OTP
    const otpsend = async () => {
        const enteredOtp = otp.join('');

        if (enteredOtp.length !== 6) {
            setError("Please enter a valid 6-digit OTP.");
            return;
        }

        try {
            const response = await axios.post('http://localhost:8000/api/verify_otp', {
                otp: enteredOtp, // Only OTP is sent, no email
            });
            alert(response.data.message);
            navigate('/CreatePassword'); // Redirect after successful verification
        } catch (err) {
            console.error("Error during OTP verification:", err);
            setError(err.response?.data?.message || "Something went wrong. Please try again.");
        }
    };

  // const otpsend = async () => {
  //   const enteredOtp = otp.join('');
  //   try {
  //     const response = await axios.post('http://localhost:8000/api/verify_otp', {
  //       email,
  //       otp: enteredOtp,
  //     });
  //     alert(response.data.message);
  //     navigate('/CreatePassword');
  //   } catch (err) {
  //     setError(err.response?.data?.message || 'Something went wrong.');
  //   }
  // };

  const verifyOtp = async () => {
    try {
      const response = await axios.post('http://localhost:8000/api/verify-otp', { email, otp });
      alert(response.data.message);
      setStep(1);
    } catch (err) {
      alert(err.response.data.message || 'Error verifying OTP');
    }
  };

  return (
    <div className="window-container">
      <div className="split-layout">
        <div className="image-side3">
          <div className="blue-overlay"></div>
        </div>
        <div className="form-side">
          <div className="form-container">
            <h1 className="title">OTP</h1>
            <div className="image-container">
              <img src={otpfile} height={150} alt="OTP Verification"/>
            </div>
            <h2 className="verification-title">Verification Code</h2>
            <p className="verification-text">
              We have sent a Verification code<br />
              to your <b>Email Address</b>
            </p>

            <div className="otp-container">
              {otp.map((digit, index) => (
                <input
                  key={index}
                  type="text"
                  maxLength="1"
                  value={digit}
                  onChange={(e) => handleChange(index, e.target.value)}
                  ref={(el) => inputRefs.current[index] = el}
                  className="otp-input"
                />
              ))}
            </div>

            {error && <p className="error-message">{error}</p>}
            <button type="submit" className="primary-button" onClick={otpsend}>
              Verify OTP
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default OTPVerification;
