                  name="name"
                  placeholder="FULL NAME"
                  className="form-input"
                  onChange={handleInputChange}
                />
              </div>
              <div className="input-group">
                <input
                  type="tel"
                  name="phone"
                  placeholder="PHONE NUMBER"
                  className="form-input"
                  onChange={handleInputChange}
                />
              </div>
              <div className="input-group">
                <input
                  type="email"
                  name="email"
                  placeholder="EMAIL"
                  className="form-input"
                  onChange={handleInputChange}
                />
              </div>
