import React, { useState } from 'react';
import './styles.css';
import { useNavigate } from 'react-router-dom';

function Login() {
    const [formData, setFormData] = useState({
        email: '',
        password: ''
    });

    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    }; 

    const handleSubmit = (e) => {
        e.preventDefault();

        fetch('http://localhost:8000/api/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(formData),
        })
        .then(async (response) => {
            if (response.ok) {
                const data = await response.json();
                localStorage.setItem('token', data.token);
                navigate(data.redirectTo);
            } else {
                const errorData = await response.json();
                console.error('Login failed:', errorData);
                alert('Login failed: ' + errorData.message);
            }
        })
        .catch((error) => {
            console.error('An error occurred:', error);
            alert('An unexpected error occurred. Please try again.');
        });
    };

    const forgot = () => {
        navigate('/ForgotPassword');
    };
  
    return (
        <div className="window-container">
            <div className="split-layout">
                <div className="image-side">
                    <div className="blue-overlay"></div>
                </div>
                <div className="form-side">
                    <div className="form-container">
                        <h1 className="title">Log In</h1>
                        <p className="subtitle">Welcome Back</p>
                        
                        <form onSubmit={handleSubmit}>
                            <div className="input-group">
                                <input
                                    type="email"
                                    name="email"
                                    placeholder="EMAIL"
                                    className="form-input"
                                    onChange={handleChange}
                                    required
                                />
                            </div>
                            <div className="input-group">
                                <input
                                    type="password"
                                    name="password"
                                    placeholder="PASSWORD"
                                    className="form-input"
                                    onChange={handleChange}
                                    required
                                />
                                <div className="forgot-link">
                                    <a href="/ForgotPassword" onClick={forgot}>FORGOT PASSWORD?</a>
                                </div>
                            </div>
                            <button type="submit" className="primary-button">
                                Log In
                                <span className="arrow">â†’</span>
                            </button>
                        </form>

                        <div className="social-section">
                            <p className="social-divider">OR CONNECT WITH SOCIAL MEDIA</p>
                            
                            <button className="social-button instagram">
                                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-instagram" viewBox="0 0 16 16">
                                    <path d="M8 0C5.829 0 5.556.01 4.703.048 3.85.088 3.269.222 2.76.42a3.9 3.9 0 0 0-1.417.923A3.9 3.9 0 0 0 .42 2.76C.222 3.268.087 3.85.048 4.7.01 5.555 0 5.827 0 8.001c0 2.172.01 2.444.048 3.297.04.852.174 1.433.372 1.942.205.526.478.972.923 1.417.444.445.89.719 1.416.923.51.198 1.09.333 1.942.372C5.555 15.99 5.827 16 8 16s2.444-.01 3.298-.048c.851-.04 1.434-.174 1.943-.372a3.9  3.9 0 0 0 1.417-.923 3.9 3.9 0 0 0 .923-1.417c.198-.51.333-1.09.372-1.943C15.99 10.444 16 10.172 16 8.001c0-2.172-.01-2.444-.048-3.297-.04-.852-.174-1.433-.372-1.942a3.9 3.9 0 0 0-.923-1.417A3.9 3.9 0 0 0 13.24.42C12.731.222 12.15.088 11.297.048 10.444.01 10.172 0 8 0zm0 1.5c2.206 0 2.444.01 3.305.045.86.035 1.38.155 1.693.26.36.116.617.26.84.483.223.223.367.48.483.84.105.313.225.833.26 1.693.035.861.045 1.099.045 3.305s-.01 2.444-.045 3.305c-.035.86-.155 1.38-.26 1.693a2.5 2.5 0 0 1-.483.84 2.5 2.5 0 0 1-.84.483c-.313.105-.833.225-1.693.26C10.444 14.99 10.206 15 8 15s-2.444-.01-3.305-.045c-.86-.035-1.38-.155-1.693-.26a2.5 2.5 0 0 1-.84-.483 2.5 2.5 0 0 1-.483-.84c-.105-.313-.225-.833-.26-1.693C1.01 10.444 1 10.206 1 8s.01-2.444.045-3.305c.035-.86.155-1.38.26-1.693a2.5 2.5 0 0 1 .483-.84 2.5 2.5 0 0 1 .84-.483c.313-.105.833-.225 1.693-.26C5.556 1.01 5.827 1 8 1zm0 3.5a4.5 4.5 0 1 0 0 9 4.5 4.5 0 0 0 0-9zm0 1a3.5 3.5 0 1 1 0 7 3.5 3.5 0 0 1 0-7z"/>
                                </svg>
                                Instagram
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Login;
