import React, { useEffect, useState } from 'react';
import { Pie } from 'react-chartjs-2';
import settingImage from './settingimage.jpg';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import axios from 'axios';
import { format, isSunday, eachWeekOfInterval } from 'date-fns';
import jsPDF from 'jspdf';
import 'jspdf-autotable';

ChartJS.register(ArcElement, Tooltip, Legend);

const TaskDashboard = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [task, setTasks] = useState([]);
  const [filteredTasks, setFilteredTasks] = useState([]);
  const [attendanceStats, setAttendanceStates] = useState({ present: 0, absent: 0 });
  const [users, setUsers] = useState([]);

  const holidays = [
    '2025-01-01', // New Year's Day
    '2025-01-26', // Republic Day
    '2025-03-17', // Holi
    '2025-04-14', // Dr. Ambedkar Jayanti
    '2025-05-01', // Labour Day
    '2025-08-15', // Independence Day
    '2025-10-02', // Gandhi Jayanti
    '2025-11-01', // Diwali
    '2025-12-25'  // Christmas
  ];

  const sundays = eachWeekOfInterval({
    start: new Date(2025, 0, 1),
    end: new Date(2025, 11, 31)
  }).map(date => format(date, 'yyyy-MM-dd'));

  const allHolidaysAndSundays = [...holidays, ...sundays];

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  useEffect(() => {
    // Fetch tasks and users
    axios.get("http://localhost:8000/api/tasks")
      .then(response => {
        setTasks(response.data);
        setFilteredTasks(response.data);
      })
      .catch(err => console.log(err));

    axios.get("http://localhost:8000/api/user-details")
      .then(response => {
        setUsers(response.data);
      })
      .catch(err => console.log(err));
  }, []);

  useEffect(() => {
    const present = task.filter(task => task.startTime && task.endTime).length;
    const absent = task.length - present;
    setAttendanceStates({ present, absent });
  }, [task]);

  const pieChartData = {
    labels: ['Present', 'Absent'],
    datasets: [
      {
        data: [attendanceStats.present, attendanceStats.absent],
        backgroundColor: ['#2E7D32', '#98FB98'],
        borderWidth: 0
      }
    ]
  };

  const pieChartOptions = {
    plugins: {
      legend: {
        position: 'bottom'
      }
    }
  };

  const isHoliday = (date) => {
    return allHolidaysAndSundays.includes(format(date, 'yyyy-MM-dd'));
  };

  const downloadPDF = () => {
    const doc = new jsPDF();
    doc.text('My Attendance Report', 14, 10);
    doc.autoTable({
      head: [['Date', 'In Time', 'Out Time']],
      body: task.map(record => {
        const recordDate = format(new Date(record.createdAt), 'dd-MM-yyyy');
        const holiday = isHoliday(new Date(record.createdAt));
        const sunday = isSunday(new Date(record.createdAt));
        if (holiday) {
          return [recordDate, 'Holiday', 'Holiday'];
        } else if (sunday) {
          return [recordDate, 'Sunday', 'Sunday'];
        } else {
          return [
            recordDate,
            format(new Date(record.startTime), 'HH:mm'),
            format(new Date(record.endTime), 'HH:mm')
          ];
        }
      })
    });
    doc.save('Attendance_Report_2025.pdf');
  };

  const handleSearch = (event) => {
    event.preventDefault();
    const searchedUser  = users.find(user => user.name.toLowerCase() === searchTerm.toLowerCase());
    if (searchedUser ) {
      const searchedTasks = task.filter(task => task.name === searchedUser.name);
      setFilteredTasks(searchedTasks);
    } else {
      setFilteredTasks([]);
    }
  };

  return (
    <div style={styles.container}>
      <nav style={styles.nav}>
        <div style={ styles.navItem}>Task Dashboard</div>
        <form onSubmit={handleSearch} style={styles.searchForm}>
          <input
            type="text"
            value={searchTerm}
            onChange={handleInputChange}
            placeholder="Search User"
            style={styles.searchInput}
          />
          <button type="submit" style={styles.searchButton}>Search</button>
        </form>
      </nav>
      <div style={styles.chartContainer}>
        <Pie data={pieChartData} options={pieChartOptions} />
      </div>
      <button onClick={downloadPDF} style={styles.downloadButton}>Download PDF</button>
      <div style={styles.taskList}>
        {filteredTasks.length > 0 ? filteredTasks.map(task => (
          <div key={task.id} style={styles.taskItem}>
            <h3>{task.name}</h3>
            <p>Start Time: {format(new Date(task.startTime), 'HH:mm')}</p>
            <p>End Time: {format(new Date(task.endTime), 'HH:mm')}</p>
          </div>
        )) : (
          <p>No tasks found for the searched user.</p>
        )}
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    fontFamily: 'Arial, sans-serif'
  },
  nav: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '20px'
  },
  navItem: {
    fontSize: '24px',
    fontWeight: 'bold'
  },
  searchForm: {
    display: 'flex'
  },
  searchInput: {
    padding: '10px',
    border: '1px solid #ccc',
    borderRadius: '4px',
    marginRight: '10px'
  },
  searchButton: {
    padding: '10px',
    backgroundColor: '#007BFF',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer'
  },
  chartContainer: {
    marginBottom: '20px'
  },
  downloadButton: {
    padding: '10px',
    backgroundColor: '#28A745',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    marginBottom: '20px'
  },
  taskList: {
    border: '1px solid #ccc',
    borderRadius: '4px',
    padding: '10px'
  },
  taskItem: {
    marginBottom: '10px',
    padding: '10px',
    border: '1px solid #eee',
    borderRadius: '4px'
  }
};

export default TaskDashboard;
