import React, { useState, useEffect } from "react";
import axios from "axios";
// import "./App.css";

const API_URL = "http://localhost:5000/tasks";

function App() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({
    task: "",
    description: "",
    learning: "",
    timeSlots: [],
  });
  const [timeSlot, setTimeSlot] = useState({ startTime: "", endTime: "", note: "" });

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const { data } = await axios.get(API_URL);
      setTasks(data);
    } catch (err) {
      console.error("Error fetching tasks:", err);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewTask({ ...newTask, [name]: value });
  };

  const handleTimeSlotChange = (e) => {
    const { name, value } = e.target;
    setTimeSlot({ ...timeSlot, [name]: value });
  };

  const addTask = async () => {
    try {
      const taskToSave = { ...newTask, timeSlots: [timeSlot] };
      await axios.post(API_URL, taskToSave);
      setNewTask({ task: "", description: "", learning: "", timeSlots: [] });
      setTimeSlot({ startTime: "", endTime: "", note: "" });
      fetchTasks();
    } catch (err) {
      console.error("Error adding task:", err);
    }
  };

  const updateTaskStatus = async (id, status) => {
    try {
      await axios.put(`${API_URL}/${id}`, { status });
      fetchTasks();
    } catch (err) {
      console.error("Error updating task:", err);
    }
  };

  const deleteTask = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`);
      fetchTasks();
    } catch (err) {
      console.error("Error deleting task:", err);
    }
  };

  return (
    <div className="App">
      <h1>Task Manager</h1>
      <div className="task-form">
        <input
          name="task"
          placeholder="Task Name"
          value={newTask.task}
          onChange={handleInputChange}
        />
        <textarea
          name="description"
          placeholder="Task Description"
          value={newTask.description}
          onChange={handleInputChange}
        />
        <textarea
          name="learning"
          placeholder="Learning"
          value={newTask.learning}
          onChange={handleInputChange}
        />
        <div className="time-slot">
          <input
            name="startTime"
            type="time"
            value={timeSlot.startTime}
            onChange={handleTimeSlotChange}
          />
          <input
            name="endTime"
            type="time"
            value={timeSlot.endTime}
            onChange={handleTimeSlotChange}
          />
          <input
            name="note"
            placeholder="Note"
            value={timeSlot.note}
            onChange={handleTimeSlotChange}
          />
        </div>
        <button onClick={addTask}>Add Task</button>
      </div>
      <div className="task-list">
        {tasks.map((task) => (
          <div key={task._id} className="task">
            <h2>{task.task}</h2>
            <p>{task.description}</p>
            <p>Learning: {task.learning}</p>
            <p>Status: {task.status}</p>
            {task.timeSlots.map((slot, index) => (
              <div key={index}>
                <p>
                  {slot.startTime} - {slot.endTime}: {slot.note}
                </p>
              </div>
            ))}
            <button onClick={() => updateTaskStatus(task._id, "Complete")}>Mark Complete</button>
            <button onClick={() => deleteTask(task._id)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
